import React from 'react'
import { Link} from "react-router-dom";
import { Form ,Button} from "react-bootstrap";
import { useState } from 'react';
import axios from "axios";
import { BASE_URL } from "./helper";
import { useNavigate } from "react-router-dom";
function Forgotpassword() {
    const navigate=useNavigate();
    const [flag,setflag]=useState(true);
    const [flag1,setflag1]=useState(false);
    const [user,SetUser]=useState({
        password:"",
        email:"",
        otp:""
    });
    const handleSubmit = async (e) => {
        e.preventDefault()
          const {name,value}=e.target
          SetUser ({
              ...user,
              [name]:value
          })
        };
    
    const fun1 = async(e) =>{
        e.preventDefault();
        const {email}=user;
        if(email) {
            setflag1(true);
            axios.post(BASE_URL + "/passwordmail",user).then(res=>{
              alert(res.data);
              if(res.data==="OTP SENT Succesfully"){
                setflag(false)
                setflag1(false);
              }
              else {
                setflag1(false);
              }
            });
        }
        else {
            alert("Invalid Email");
        }
    }

    const verifyotp=()=>{

    }
    function cancel(){
        window.location.reload();
      }
  return (
    <>
     {flag && <div class="d-flex justify-content-center align-items-center h-100 m-5 pp">
      <div className='m-5'>
        <h1>Reset Your Password</h1>
        <h4 className='p-2 m-1'>Enter Your Registered EmailID</h4>
        {flag1 && <h5 className="text-success ms-5 px-5">Sending OTP...</h5>}
        <Form onSubmit={fun1}>
            <Form.Group className="mt-5" controlId="formBasicEmail">
              <Form.Control
                type="email"
                name="email"
                value={user.email}
                placeholder="Email address"
                onChange={handleSubmit}
              />
            </Form.Group>
            <div className="d-grid mt-4">
              <Button variant="primary" type="Submit">
                Send OTP
              </Button>
            </div>
        </Form>
      </div>
    </div>}
    {!flag && <div className='border border-dark ppp pp p-5'>
            <h2 className="mb-3">Verify Your OTP Sent to</h2>
            <h6>{user.email}</h6>
            {flag1 && <h5 className="text-success">Sending OTP...</h5>}
             <Form >
             <Form.Group className="mb-3" controlId="formBasicOTP">
              <Form.Control
                type="password"
                placeholder="Enter OTP"
                name="otp"
                value={user.otp}
                minLength={'6'}
                onChange={handleSubmit}
              />
            </Form.Group>
            <div className="d-grid">
              <Button variant="primary" type="Submit" >
                Verify OTP
              </Button>
            </div>
             </Form>
             <div className="p-2"><Link variant="primary" type="Submit" onClick={fun1} >
                Resend OTP ? 
              </Link></div>
             <div className="p-2"><Button variant="primary" type="Submit" onClick={cancel}>
                Cancel
              </Button></div>
          </div>}
    </>
  )
}

export default Forgotpassword
