import React from 'react'
import { Form } from 'react-bootstrap'
import { useState } from 'react';
import axios from "axios";
import { BASE_URL } from "./helper";
import { useNavigate } from "react-router-dom";
function Forgotpassword() {
    const navigate=useNavigate();
    const [d,sd]=useState("");
    const [user,SetUser]=useState({
        password:"",
        email:"",
        otp:""
    });
    const handleSubmit = async (e) => {
        e.preventDefault()
          const {name,value}=e.target
          SetUser ({
              ...user,
              [name]:value
          })
        };
    
    const fun1 = async(e) =>{
        e.preventDefault();
        const {email}=user;
        if(email) {
            sd("");
            axios.post( BASE_URL + "/login",user)
         .then(res => {
          alert(res.data.message)
          sd(res.data.message);
          if(res.data.user) {localStorage.setItem('token',res.data.user.name);
          localStorage.setItem('token1',res.data.user.email);
         }
          navigate("/");
        })
        }
        else {
            alert("Invalid Email");
        }
    }
  return (
    <>
     <div class="d-flex justify-content-center align-items-center h-100 m-5 pp">
      <div className='m-5'>
        <h1>Reset Your Password</h1>
        <h4 className='p-2 m-1'>Enter Your Registered EmailID</h4>
        {d==="" && (<h6 className="text-success">Verifying user....</h6>)}
        <Form onSubmit={fun1}>
            <Form.Group className="mt-5" controlId="formBasicEmail">
              <Form.Control
                type="email"
                name="email"
                value={user.email}
                placeholder="Email address"
                onChange={handleSubmit}
              />
            </Form.Group>
        </Form>
      </div>
    </div>
    </>
  )
}

export default Forgotpassword
