import image from './Images/img1.png';
import React, { useState } from "react";
import { Link,} from "react-router-dom";
import { Form} from "react-bootstrap";
import { Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { BASE_URL } from "./helper";
function Signup() {
  localStorage.removeItem('token');
  localStorage.removeItem('token1');
  const navigate=useNavigate();
    const [user,SetUser]=useState({
        name:"",
        email:"",
        password:"",
        otp:"",
        role:"",
        company:"",
        department:"",
        cpassword:""
    });
    const [flag,setflag]=useState(true);
    const [flag1,setflag1]=useState(false);
    const handleSubmit = async (e) => {
      e.preventDefault()
        const {name,value}=e.target
        SetUser ({
            ...user,
            [name]:value
        })
      };

      const signup2 =async (e) => {
        e.preventDefault();
          const {name,email,password,otp,epassword}=user;
          if(password!==c)
          if(name && email && password && password.length>=6 && otp.length===6) {
             axios.post( BASE_URL + "/signup",user)
             .then(res => {
              if(res.data.message==="SuccessFully Registered") {
                alert(res.data.message);
                localStorage.setItem('token',name);
                localStorage.setItem('token1',email);
              }
              else if(res.data==="Invalid OTP") {
                alert(res.data);
                navigate("/")
              }
              navigate("/");
            })
          }
          else {
            alert("Invalid Input");
            navigate("/")
          }
      }
      const signup1 = async(e)=>{
        e.preventDefault(e);
        const {email,name,password}=user;
        if(name && email && password && password.length>=6) {
            setflag1(true);
            axios.post(BASE_URL + "/makemail",user).then(res=>{
              alert(res.data);
              if(res.data==="OTP SENT Succesfully"){
                setflag(false)
                setflag1(false);
              }
            });
        }
        else {
            alert("Invalid Input");
        }
      }

      function cancel(){
        window.location.reload();
      }
  return (
    <>
      { flag && <div class="container mx-auto m-5 pt-2 pp">
        <div class="row justify-content-center">
            <div class="col-5">
                <img src={image} alt=""></img>
            </div>
            <div class="col-5 ms-5 mt-5 p-2">
            <h1 className="">Sign up</h1>
            <div>Have have an account? <Link to="/Login">Log in</Link></div>
            <Form >
            <div className="row">
                <div class='col-6'>
                <Form.Group className="mt-4" controlId="formBasicEmail">
              <Form.Control
                type="text"
                name="Full Name"
                value={user.name}
                placeholder="Full Name"
                onChange={handleSubmit}
              />
            </Form.Group>

                </div>
                <div class='col-6'>
                <Form.Group className="mt-4" controlId="formBasicEmail">
              <Form.Control
                type="text"
                name="company"
                value={user.company}
                placeholder="Company"
                onChange={handleSubmit}
              />
            </Form.Group>

                </div>
            </div>
            <div className="row">
                <div class='col-6'>
                <Form.Group className="mt-4" controlId="formBasicEmail">
              <Form.Control
                type="email"
                name="email"
                value={user.email}
                placeholder="Email address"
                onChange={handleSubmit}
              />
            </Form.Group>

                </div>
                <div class='col-6'>
                <Form.Group className="mt-4" controlId="formBasicEmail">
              <Form.Control
                type="text"
                name="Role"
                value={user.role}
                placeholder="Role / Title"
                onChange={handleSubmit}
              />
            </Form.Group>

                </div>
            </div>
            <div className="row">
                <div class='col-6'>
                <Form.Group className="mt-4" controlId="formBasicEmail">
              <Form.Control
                type="text"
                name="department"
                value={user.department}
                placeholder="Department"
                onChange={handleSubmit}
              />
            </Form.Group>

                </div>
                <div class='col-6'>
                <Form.Group className="mt-4" controlId="formBasicPassword">
                    <Form.Control
                type="password"
                name="password"
                value={user.password}
                placeholder="Password"
                onChange={handleSubmit}
                />
                </Form.Group>
                </div>
            </div>
            <div class="row">
            <Form.Group className="mt-4" controlId="formBasicPassword">
                <Form.Control
                type="password"
                name="Confirm password"
                value={user.cpassword}
                placeholder="Confirm Password"
                onChange={handleSubmit}/>
            </Form.Group>
            </div>
            <div className="d-grid mt-4">
              <Button variant="primary" type="Submit" onClick={signup1}>
                Sign Up
              </Button>
            </div>
            </Form>
            </div>
        </div>
    </div>}
          {!flag && <div>
            <h2 className="mb-3">Verify Your OTP Sent to</h2>
            <h6>{user.email}</h6>
             <Form onSubmit={signup2}>
             <Form.Group className="mb-3" controlId="formBasicOTP">
              <Form.Control
                type="password"
                placeholder="Enter OTP"
                name="otp"
                value={user.otp}
                minLength={'6'}
                onChange={handleSubmit}
              />
            </Form.Group>
            <div className="d-grid gap-2">
              <Button variant="primary" type="Submit" onClick={signup2}>
                Verify OTP
              </Button>
            </div>
             </Form>
             <div className="p-3"><Button variant="primary" type="Submit" onClick={cancel}>
                Cancel
              </Button></div>
          </div>}
      </>
  )
}

export default Signup;
