import React, { useState } from "react";
import { Link} from "react-router-dom";
import { Form} from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { Button } from "react-bootstrap";
import './Login.css';
import axios from "axios";
import { BASE_URL } from "./helper";
import image from './Images/img1.png';

const Signup=( )=> {
  localStorage.removeItem('token');
  localStorage.removeItem('token1');
  const navigate=useNavigate();
  const [d,sd]=useState("1");
  const [user,SetUser]=useState({
      password:"",
      email:"",
      name:""
  });

  const handleSubmit = async (e) => {
    e.preventDefault()
      const {name,value}=e.target
      SetUser ({
          ...user,
          [name]:value
      })
    };
    const login1=async(e)=>{
      e.preventDefault();
      const {email,password}=user
      if(email && password ) {
        sd("");
         axios.post( BASE_URL + "/login",user)
         .then(res => {
          alert(res.data.message)
          sd(res.data.message);
          if(res.data.user) {localStorage.setItem('token',res.data.user.name);
          localStorage.setItem('token1',res.data.user.email);
         }
          navigate("/");
        })
      }
      else {
        alert("Invalid Input");
      }
    }
     return (
      <>
      <div class="container mx-auto m-5 pt-2 pp">
        <div class="row justify-content-center">
            <div class="col-5">
                <img src={image} alt=""></img>
            </div>
            <div class="col-5 ms-5 mt-5 p-2">
            <h1 className="">Login </h1>
            {d==="" && (<h6 className="text-success">Verifying user....</h6>)}
            <div>Have have an account? <Link to="/Login">Log in</Link></div>
            <Form onSubmit={login1}>
            <div class>

            </div>
            <div className="d-grid mt-4">
              <Button variant="primary" type="Submit">
                Log In
              </Button>
            </div>
            </Form>
            </div>
        </div>
    </div>
      
      </>
    )
}

export default Signup;
